create table product_collect(id BIGINT(13) UNSIGNED NOT NULL PRIMARY KEY AUTO_INCREMENT,productID varchar(1024) NOT NULL,collectTime datetime NOT NULL,machineID varchar(128) NOT NULL,networkID varchar(25),userID varchar(320) NOT NULL,softwareVersion varchar(64),softwareName varchar(64),firmWareVersion varchar(64),referID varchar(1024),status tinyint(4) NOT NULL) ENGINE=InnoDB DEFAULT CHARSET=utf8;